// Safely access the item that triggered this workflow
const sourceItem = $('When Executed by Another Workflow').first().json;

// Access alertIds inside the "AI Analyze " property (note the space!)
const alertIds = sourceItem['AI Analyze ']?.alertIds || [];

// Format each alertId into an object
const formatted = alertIds.map(id => ({ alertId: id }));

// Return a single item with the array of objects
return [
  {
    json: {
      alertIds: formatted
    }
  }
];
