// ðŸ”¹ Get the input object
const inputArray =$('When Executed by Another Workflow').first().json['Final AI Result'] ;
const input = Array.isArray(inputArray) ? inputArray[0] : inputArray;

// ðŸ”¹ Your original sanitize rules â€” keep them as requested
function sanitize(str) {
  if (typeof str !== 'string') return str;
  return str
    .replace(/\\/g, '\\\\')  // Escape backslashes
    .replace(/\n/g, '\\n')   // Replace newlines with literal \n
    .replace(/\//g, '//')    // Escape forward slashes
    .replace(/"/g, '\\"');   // Escape double quotes
}

// ðŸ”¹ Extract AI_result and AI_Confidence if pattern matches
function extractAIResult(value) {
  if (typeof value !== 'string') return null;
  const match = value.match(/^(.+?)\s*\((\d+(?:\.\d+)?)%\)$/);
  if (match) {
    return {
      AI_result: match[1].trim(),
      AI_Confidence: parseFloat(match[2])
    };
  }
  return null;
}

// ðŸ”¹ Recursive sanitize + result extraction
function deepSanitize(value) {
  if (Array.isArray(value)) {
    return value.map(deepSanitize);
  } else if (value && typeof value === 'object') {
    const sanitizedObj = {};
    for (const [key, val] of Object.entries(value)) {
      const sanitizedVal = deepSanitize(val);

      // Handle "result" specially
      if (key === 'result' && typeof sanitizedVal === 'string') {
        const extracted = extractAIResult(sanitizedVal);
        if (extracted) {
          sanitizedObj['AI_result'] = sanitize(extracted.AI_result);
          sanitizedObj['AI_Confidence'] = extracted.AI_Confidence;
          continue;
        }
      }

      sanitizedObj[key] = sanitizedVal;
    }
    return sanitizedObj;
  } else if (typeof value === 'string') {
    return sanitize(value);
  } else {
    return value;
  }
}

// ðŸ”¹ Apply recursively
const sanitized = deepSanitize(input);

// ðŸ”¹ Return same structure as input (avoid double-escape by not stringifying)
return [
  {
    json: {
      'Final AI Result': [sanitized]
    }
  }
];

