const data = $input.all();

let sourceip = [];
let destinationip = [];
let domains = [];

// Function to validate IP address format
function isValidIp(ip) {
  const ipRegex = /^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
  return ipRegex.test(ip);
}

// Function to check if IP is public
function isPublicIp(ip) {
  const privateIpRanges = [
    /^10\./,
    /^172\.(1[6-9]|2[0-9]|3[0-1])\./,
    /^192\.168\./,
    /^127\./,
    /^169\.254\./,
  ];
  return !privateIpRanges.some(range => range.test(ip));
}

for (const item of data) {
  const rule = item.json['Original Rule'];

  if (rule) {
    // Source IPs
    const srcIps = [rule.src_ip, rule.src];
    for (const ip of srcIps) {
      if (ip && ip !== "null" && isValidIp(ip) && isPublicIp(ip)) {
        sourceip.push(ip);
      }
    }

    // Destination IPs
    const destIps = [rule.dest_ip, rule.dest];
    for (const ip of destIps) {
      if (ip && ip !== "null" && isValidIp(ip) && isPublicIp(ip)) {
        destinationip.push(ip);
      }
    }

    // Domains
    const url = rule.url;
    if (url && url !== "null") {
      domains.push(url);
    }
  }
}

// Join and ensure empty string if no values
const SourceIP = sourceip.length ? sourceip.join(", ") : "";
const DestinationIP = destinationip.length ? destinationip.join(", ") : "";
const Domains = domains.length ? domains.join(", ") : "";

return [{ json: { SourceIP, DestinationIP, Domains } }];

// Initialize an array to hold the related alerts
const TIResult = [];

// Get input data
const items = $input.all(); // Get all input items

// Function to safely extract values from input data and escape newlines
function getValue(fieldName) {
  const item = items.find(i => i.json && i.json[fieldName] !== undefined);
  return item ? String(item.json[fieldName]).replace(/\n/g, '\\n') : null; // Escape \n
}

// Extract values using the function
const AbuseSourceIP = getValue('AbuseIPDB Result for SourceIP');
const SourceIPNotAbuse = getValue('Source IP not found on Abuse IP');
const MISPSourceIP = getValue('MISP Result for Source IP');
const sourceIPNotMISP = getValue('source IP not found on MISP');
const AbuseDestinationIP = getValue('AbuseIPDB Result for DestinationIP');
const DestinationIPNotAbuse = getValue('Destination IP not found on Abuse IP'); // Fixed typo
const MISPDestinationIP = getValue('MISP Result for Destination IP');
const DestinationIPNotMISP = getValue('Destination IP not found on MISP');
const MISPURL = getValue('MISP Result for URL');
const URLNotMISP = getValue('URL not found on MISP');
const AlienVaultURL = getValue('AlienVault Result for URL');
const URLNotAlienVault = getValue('URL not found on AlienVault');

// Helper function to add alert if value exists
function addAlert(value, name) {
  if (value) {
    TIResult.push({
      Result: value,
      Source: name
    });
  }
}

// Add alerts to array
addAlert(AbuseSourceIP, "Abuseipdb");
addAlert(SourceIPNotAbuse, "Abuseipdb");
addAlert(MISPSourceIP, "MISP");
addAlert(sourceIPNotMISP, "MISP");
addAlert(AbuseDestinationIP, "Abuseipdb");
addAlert(DestinationIPNotAbuse, "Abuseipdb");
addAlert(MISPDestinationIP, "MISP");
addAlert(DestinationIPNotMISP, "MISP");
addAlert(MISPURL, "MISP");
addAlert(URLNotMISP, "MISP");
addAlert(AlienVaultURL, "AlienVault");
addAlert(URLNotAlienVault, "AlienVault");

// Construct the final JSON object
const result = {
  TIResult: TIResult,
  size: TIResult.length // Make size dynamic
};

// Return the result in n8n format
return result;

