// Try to get the input data from the first item
let inputData;
try {
	inputData = $input.first().json._source;
} catch (e) {
	throw new Error("Can't get timeline notes from input[0]");
}

// Try to get Kibana results from input[2]
let kibanaResults;
try {
	kibanaResults = $input.all()[2].json.results;
} catch (e) {
	kibanaResults = [];
}

// Try to get rule query from input[1]
let ruleQuery;
try {
	ruleQuery = $input.all()[1].json.CleanOriginalRule?.[0]?.kibana_alert_rule_parameters_query;
} catch (e) {
	ruleQuery = undefined;
}

// Function to escape special characters
const escapeSpecialCharacters = (text) => {
	if (text) {
		return text
			.replace(/\\/g, '\\\\')
			.replace(/\n/g, '\\n')
			.replace(/\r/g, '\\r')
			.replace(/\t/g, '\\t')
			.replace(/\"/g, '\\"')
			.replace(/\//g, '\\/');
	}
	return text;
};

// Extract notes from the flat structure
const eiNotes = escapeSpecialCharacters(inputData.EI || '');
const ticketingNotes = escapeSpecialCharacters(inputData.Ticket || '');
const tiNotes = escapeSpecialCharacters(inputData.TI || '');
const relatedAlertNotes = escapeSpecialCharacters(inputData.RA || '');
const relatedLogsNotes = escapeSpecialCharacters(inputData.RL || '');

// Build final result
const finalResult = {};

if (eiNotes.trim()) finalResult.EI = eiNotes.trim();
if (ticketingNotes.trim()) finalResult.ticketing = ticketingNotes.trim();
if (tiNotes.trim()) finalResult.TI = tiNotes.trim();
if (relatedAlertNotes.trim()) finalResult.related_alerts = relatedAlertNotes.trim();
if (relatedLogsNotes.trim()) finalResult.related_logs = relatedLogsNotes.trim();

if (ruleQuery && typeof ruleQuery === 'string') {
	finalResult.rule_query = escapeSpecialCharacters(ruleQuery.trim())
		.replace(/\/\*/g, '\\/*')
		.replace(/\*\//g, '*\\/');
}

// Add Kibana vulnerability results
try {
	finalResult.kibana_results = $('Search Kibana').first().json.aggregations.by_host.buckets;
} catch (e) {
	finalResult.kibana_results = [];
}

return [{ json: { Final: finalResult } }];

