// گرفتن داده از نود Search Kibana
const agg = $('Search Kibana').first().json.aggregations;

// بررسی وجود aggregation
if (!agg || !agg['by_host.ip'] || !agg['by_host.ip'].buckets) {
  return [{ json: { results: [] } }];
}

// پردازش bucketها
const processedResults = agg['by_host.ip'].buckets.flatMap(bucket => {
  const ip = bucket.key || 'N/A';
  const hits = bucket['top_risks'].hits?.hits || [];

  return hits.map(hit => {
    const source = hit._source || {};
    return {
      host: ip,
      vulnerability_id: source['vulnerability.id'] || 'N/A',
      risk: source['vulnerability.severity'] || 'N/A',
      description: source['vulnerability.description'] || 'N/A',
      plugin_output: source['nessus.plugin_output'] || 'N/A'
    };
  });
});

// خروجی
return [
  {
    json: {
      results: processedResults
    }
  }
];

// Retrieve the data array from the previous node
const data = $input.all();

// Initialize empty arrays to store IPs
let sensorip = [];
let destinationip = [];
let serverip = [];

// Loop through each item in the data array
for (const item of data) {
  // Check if the item has a 'sensor.ip' field
  const sensorIp = item.json?.['Original Rule']?.[0]?.json?.latest_doc?.hits?.hits[0]?._source?.['sensor.ip'];
  if (sensorIp) {
    sensorip.push(sensorIp);
  }

  // Check if the item has a 'destination.ip' field
  const destinationIP = item.json?.['Original Rule']?.[0]?.json?.latest_doc?.hits?.hits[0]?._source?.destination?.ip;
  if (destinationIP) {
    destinationip.push(destinationIP);
  }

  // Check if the item has a 'host.ip' field (for Server IP)
  const serverIp = item.json?.['Original Rule']?.[0]?.json?.latest_doc?.hits?.hits[0]?._source?.host?.ip;
  if (serverIp) {
    serverip.push(serverIp);
  }
}

// Join all IPs into a single string, separated by a delimiter (e.g., comma)
const SensorIP = sensorip.join(", ");
const DestinationIP = destinationip.join(", ");
const ServerIP = serverip.join(", ");

// Return all the collected data in a single JSON object
return [{ json: { SensorIP, DestinationIP, ServerIP } }];
