const input = $('When Executed by Another Workflow').first().json['Final AI Result'][0];

const result = [];

// Helper function to add items with appropriate type
function addItemIfExists(items, itemType) {
    if (Array.isArray(items)) {
        items.forEach(item => {
            result.push({
                value: item,
                type: itemType
            });
        });
    }
}

// Function to check if a string is an IPv4 address
function isIPv4(ip) {
    return /^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$/.test(ip);
}

// Process attackerIps
if (Array.isArray(input.attackerIps)) {
    input.attackerIps.forEach(ip => {
        const type = isIPv4(ip.trim()) ? 'IP' : 'Host Name';
        result.push({ value: ip, type });
    });
}

// Process targetIps
if (Array.isArray(input.targetIps)) {
    input.targetIps.forEach(ip => {
        const type = isIPv4(ip.trim()) ? 'IP' : 'Host Name';
        result.push({ value: ip, type });
    });
}

// Add users
addItemIfExists(input.users, 'User Name');
addItemIfExists(input.targetNames, 'Domain');

// Add urls
addItemIfExists(input.urls, 'URL');

// Add processes
addItemIfExists(input.processes, 'Process');

return [{ json: { result } }];

// Get the input value from n8n
const inputDate = $input.first().json.currentDate;

// Convert the input string to a JavaScript Date object
const dateObj = new Date(inputDate);

// Get the year, month, day, hours, minutes, seconds, and milliseconds
const year = dateObj.getFullYear();
const month = String(dateObj.getMonth() + 1).padStart(2, '0');
const day = String(dateObj.getDate()).padStart(2, '0');
const hour = String(dateObj.getHours()).padStart(2, '0');
const minute = String(dateObj.getMinutes()).padStart(2, '0');
const second = String(dateObj.getSeconds()).padStart(2, '0');
const millisecond = String(dateObj.getMilliseconds()).padStart(3, '0');

// Set the fixed timezone offset manually (in this case, +05:30)
const fixedOffset = "+0530";

// Construct the formatted date string for Jira DateTime Picker with the fixed offset
const jiraFormattedDate = `${year}-${month}-${day}T${hour}:${minute}:${second}.${millisecond}${fixedOffset}`;

// Output the result
return {
  json: {
    jiraFormattedDate
  }
};

// Extract Observable_key values
const keys = items.map(item => item.json.Observable_key);

// Remove duplicates using Set
const uniqueKeys = [...new Set(keys)];

// Return the unique keys in a single item
return [
  {
    json: {
      Observable_keys: uniqueKeys
    }
  }
];

