// === READ VARIABLES ===
const esMultiJira = String($vars.ES_MultiJiraProject).toLowerCase() === "true";
const esMultiZone = String($vars.ES_MultiZone).toLowerCase() === "true";
const jiraProjectKey = $vars.JIRA_Project_Key_Field;

// === TRY TO READ NAMESPACE SAFELY ===
let namespace;

try {
  // Use .first().json (confirmed from your working version)
  const sourceNode = $('When Executed by Another Workflow').first().json;

  namespace =
    sourceNode?.['Original Rule']?.[0]?.json?.latest_doc?.hits?.hits?.[0]?._source?.data_stream?.namespace ||
    null;
} catch (err) {
  namespace = null;
}

// Default fallback
if (!namespace) namespace = 'Unknown_Namespace';

// === APPLY LOGIC ===
let Project_Key;
let Lable;

if (esMultiJira === true) {
  // Condition 1: MultiJiraProject = true
  Project_Key = namespace;
  Lable = namespace;
} else if (esMultiZone === true) {
  // Condition 2: MultiJiraProject = false, MultiZone = true
  Project_Key = jiraProjectKey;
  Lable = namespace;
} else {
  // Condition 3: both false
  Project_Key = jiraProjectKey;
  Lable = jiraProjectKey;
}

// === REPLACE "default" VALUES WITH JIRA PROJECT KEY ===
if (String(Project_Key).toLowerCase() === "default") {
  Project_Key = jiraProjectKey;
}

if (String(Lable).toLowerCase() === "default") {
  Lable = jiraProjectKey;
}

// === DEBUG LOGS (visible in n8n execution logs) ===
console.log('ES_MultiJiraProject:', esMultiJira);
console.log('ES_MultiZone:', esMultiZone);
console.log('Namespace:', namespace);
console.log('Project_Key:', Project_Key);
console.log('Lable:', Lable);

// === RETURN OUTPUT ===
return [
  {
    json: {
      Project_Key,
      Lable,
    },
  },
];
